mapper.update:
update
tm_yca_detail
set name = #{name,jdbcType=VARCHAR},
unit = #{unit,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
    select 

    from tm_lookvalue t
    where 1 = 1  

		and t.looktype_id = #{map.lookTypeId,jdbcType=INTEGER}   


		and t.lookvalue_id = #{map.lookvalue_id,jdbcType=INTEGER}
