mapper.select:
select ,t.tax_Price,t.notax_Price, convert(sum(t.amount),decimal(14,2)) as amount, convert(sum(t.tax_Single_SumPrice),decimal(14,2)) as tax_Single_SumPrice, convert(sum(t.single_SumPrice),decimal(14,2)) as single_SumPrice from tm_unitproject_detail t inner join tm_unitproject tu on t.unitproject_id = tu.id inner join tm_lookvalue tl on t.lookvalueid = tl.lookvalue_id inner join tm_looktype tlt on tlt.looktype_id = tl.looktype_id
and tlt.looktype_id = #{map.lookTypeId,jdbcType=INTEGER}
and tu.bit_project_id = #{map.bitProjectId,jdbcType=INTEGER}
group by t.code, t.type, t.name, t.unit, t.tax_Price,t.notax_Price

select count(1) from ( select ,t.tax_Price,t.notax_Price, convert(sum(t.amount),decimal(14,2)) as amount, convert(sum(t.tax_Single_SumPrice),decimal(14,2)) as tax_Single_SumPrice, convert(sum(t.single_SumPrice),decimal(14,2)) as single_SumPrice from tm_unitproject_detail t inner join tm_unitproject tu on t.unitproject_id = tu.id inner join tm_lookvalue tl on t.lookvalueid = tl.lookvalue_id inner join tm_looktype tlt on tlt.looktype_id = tl.looktype_id
and tlt.looktype_id = #{map.lookTypeId,jdbcType=INTEGER}
and tu.bit_project_id = #{map.bitProjectId,jdbcType=INTEGER}
group by t.code, t.type, t.name, t.unit, t.tax_Price,t.notax_Price )t

select ,t.tax_Single_SumPrice,t.notax_Price,t.tax_Price,t.single_SumPrice,t.amount from tm_unitproject_detail t where 1 = 1
and t.code = #{map.code,jdbcType=VARCHAR}
and t.unitproject_id in( select id FROM tm_unitproject where bit_project_id = #{map.bitProjectId,jdbcType=INTEGER})

select

from tm_unitproject t 
inner join tm_unitproject_detail b on t.id = b.unitproject_id
where t.bit_project_id = #{map.bitProjectId,jdbcType=INTEGER}
and b.code = #{map.code,jdbcType=VARCHAR}
